define(['jquery','fab/fabrik'],function(jQuery,Fabrik){'use strict';var Consent=new Class({options:{'renderOrder':''},initialize:function(options){var self=this;this.options=jQuery.extend(this.options,options);this.form=Fabrik.getBlock('form_'+this.options.formid);Fabrik.addEvent('fabrik.form.submit.failed',function(form,event,btn){if(form===this.form){jQuery('.consentError').removeClass('fabrikHide');this.form.showMainError(this.form.options.error);}}.bind(this));Fabrik.addEvent('fabrik.form.submitted',function(form,event,btn){if(form===this.form){jQuery('.consentError').addClass('fabrikHide');}}.bind(this));}});return Consent;});